import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'

const Login = (props) => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [emailError, setEmailError] = useState('')
    const [passwordError, setPasswordError] = useState('')

    const navigate = useNavigate()

    const onButtonClick = () => {
        // Set initial error values to empty
        setEmailError('')
        setPasswordError('')

        // Check if the user has entered both fields correctly
        if ('' === email) {
            setEmailError('Please enter your email')
            return
        }

        if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
            setEmailError('Please enter a valid email')
            return
        }

        if ('' === password) {
            setPasswordError('Please enter a password')
            return
        }

        if (password.length < 7) {
            setPasswordError('The password must be 8 characters or longer')
            return
        }

        // Simulate successful login and naviagte to dashboard
        navigate('/dashboard')
    }

    const onTransactionClick = () => {
        navigate('/transactions')
    }

    const [data, setdata] = useState({
        name: "", 
        age: 0,
        data: "",
        programming: ""
      })

    useEffect(() => {
        fetch("/data").then((res) => 
          res.json().then((data) => {
            setdata({
              name: data.Name,
              age: data.Age,
              data: data.Date,
              programming: data.programming
            })
          }))
      }, [])

    return (
        <div className={'mainContainer'}>

            <div className={'titleContainer'}>
                <div>Login</div>
                <p> Welcome {data.name} </p>
            </div>
            <br />

            <div className={'inputContainer'}>
                <input
                    value={email}
                    placeholder='Enter your email here'
                    onChange={(ev) => setEmail(ev.target.value)}
                    className={'inputBox'}
                />
                <label className='errorLabel'>{emailError}</label>
            </div>
            <br />

            <div className={'inputContainer'}>
                <input
                    value={password}
                    placeholder='Enter your password here'
                    onChange={(ev) => setPassword(ev.target.value)}
                    className={'inputBox'}
                />
                <label className='errorLabel'>{passwordError}</label>
            </div>
            <br />

            <div className={'inputContainer'}>
                <input className={'inputButton'} type='button' onClick={onButtonClick} value={'Login'} />
                <input className={'transactionsButton'} type='button' onClick={onTransactionClick} value={'Manage my Transactions'} />
            </div>

        </div>  
    )
}

export default Login
