import React, { useEffect, useState } from 'react';
// import dashboard.css

const Dashboard = () => {
    const [accounts, setAccounts] = useState([]);
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // fetch data for user accounts
        const testAccounts = [
            { id: 1, type: "Checking", balance: 1523.45 },
            { id: 2, type: "Savings", balance: 10234.56 },
            { id: 3, type: "Credit", balance: -500.75 },
        ];

        // fetch data for scheduled transactions
        const testTransactions = [
            { id: 1, date: "2024-12-20", amount: 200.0, description: "Electricity Bill" },
            { id: 2, date: "2024-12-25", amount: 500.0, description: "Rent Payment" },
            { id: 3, date: "2024-12-30", amount: 100.0, description: "Subscription Service" },
        ];

        //fetch both accounts and transactions

        // Simulate data load
        setTimeout(() => {
            setAccounts([]);
            setTransactions([]);
            setLoading(false);
          }, 1000);
    }, []);

    if (loading) {
        return <div className='loading'>Loading...</div>
    }

    return (
        <div className='user-dashboard'>
            <h1>Welcome to Your Dashboard</h1>

            <section className='accounts-section'>
                <h2>Your Accounts</h2>
                {accounts.length > 0 ? (
                    <ul className='accounts-list'>
                        {accounts.map((a) => (
                            <li key={a.id} className='account-item'>
                                <div className='account-type'>{a.type}</div>
                                <div className='account-balance'>Balance: ${a.balance.toFixed(2)}</div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No accounts found.</p>
                )}
            </section>

            <section className='transactions-section'>
                <h2>Scheduled Transactions</h2>
                {transactions.length > 0 ? (
                    <ul className='transactions-list'>
                        {transactions.map((t) => (
                            <li key={t.id} className='transaction-item'>
                                <div className='transaction-date'>Date: {t.date}</div>
                                <div className='transaction-amount'>Amount: ${t.amount}</div>
                                <div className='transaction-desc'>Description: {t.description}</div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No transactions scheduled.</p>
                )}
            </section>
        </div>
    )
}

export default Dashboard;
